title.map:
function(doc) { 
      	emit(doc.title.trim(), doc); 
      	words = doc.title.replace(/[!.,;]+/g,'').toLowerCase().split(' '); 
      	for each (word in words){
      		if(word!='-'&&word!=''&&word!=':'&&word!='.'){ 
      			emit(word,doc);
      		} 
      	} 
      }

actor.map:
function(doc){
        for each(author in doc.authors) {
          emit(author.trim(), doc);
          words = author.replace(/[!.,;]+/g,'').split(' ');
          for each (word in words) {
            if(word!='-'&&word!=''&&word!=':'&&word!='.'){
              emit(word,doc);
            }
          }
        }
      }

