title_year.map:
function(doc){
        year = doc.year.trim();
        emit([doc.title.trim(), year], doc);
        words = doc.title.replace(/[!.,;]+/g,'').toLowerCase().split(' ');
        for each (word in words) {
          if(word!='-'&&word!=''&&word!=':'&&word!='.'){
            emit([word, year],doc);
          }
        }
      }

